import React, { useState, useEffect } from "react";
import "./App.css";

import { Container, Row, Table, Button, Col } from "react-bootstrap";
import SinglePost from "./components/SinglePost";

function Posts() {
  const [error, setError] = useState(null);
  const [loadingPosts, setLoadingPosts] = useState(false);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    getPosts();
  }, []);

  function getPosts() {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((res) => res.json())
      .then((results) => {
        setPosts(results);
      })
      .catch((error) => {
        setError(error);
      })
      .finally(() => setLoadingPosts(false));
  }

  // shto loader per delete
  // gjej indeksin e objektit per delete
  // pasi e gjen, update property is loading delete
  // delete sukses => getposts again
  // nese sukses mos e update sepse do vijne te dhena te reja
  // nese error beji update dhe shfaq errorin
  function handleDelete(id) {
    const newPosts = posts.map((p) => ({ ...p }));
    const index = posts.findIndex((post) => post.id === id);
    newPosts[index].isLoadingDelete = true;
    setPosts(newPosts);
    fetch(`https://jsonplaceholder.typicode.com/posts/${id}`, {
      method: "DELETE",
    })
      .then(() => {
        return Promise.reject(false);
        getPosts();
      })
      .catch((err) => {
        const newPosts = posts.map((p) => ({ ...p }));
        const index = posts.findIndex((post) => post.id === id);
        newPosts[index].isLoadingDelete = false;
        setPosts(newPosts);
      });
  }

  // ndryshosh current page
  // get posts me page numberine ri
  function handlePageChange(pageNumber) {}

  if (error) {
    return <div>Error: {error.message}</div>;
  }
  if (loadingPosts) {
    return <div>LOADING</div>;
  }
  return (
    <Container>
      <Row className="mt-4 mb-4">
        <Col className="text-center">
          <div>
            <h2>POSTS API</h2>
          </div>
        </Col>
        <Col className="text-right">
          <Button>Create new Post</Button>
        </Col>
      </Row>
      <Row>
        <Table>
          <thead>
            <tr>
              <th>User Id</th>
              <th>Post Id</th>
              <th>Title</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {posts.map((post) => (
              <SinglePost
                key={post.id}
                post={post}
                handleDelete={handleDelete}
                initialValues={{
                  userId: post.userId,
                  title: post.title,
                  body: post.body,
                }}
                isLoadingDelete={post.isLoadingDelete}
              />
            ))}
          </tbody>
        </Table>
      </Row>
    </Container>
  );
}

export default Posts;
