import React, { useState, useEffect } from "react";
import "./App.css";
import Posts from "./Posts";
import { Button } from "react-bootstrap";

export default function PostsPage() {
  const [error, setError] = useState(null);
  const [loadingPosts, setLoadingPosts] = useState(false);
  const [posts, setPosts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [postsPerPage, setPostsPerPage] = useState(5);

  useEffect(() => {
    getPosts();
  }, []);

  useEffect(() => {
    getPosts();
  }, [currentPage]);

  function getPosts(itemsPerPage = postsPerPage) {
    fetch(
      `https://jsonplaceholder.typicode.com/posts?_limit=${itemsPerPage}&_page=${currentPage}`
    )
      .then((res) => res.json())
      .then((results) => {
        setPosts(results);
      })
      .catch((error) => {
        setError(error);
      })
      .finally(() => setLoadingPosts(false));
  }

  function isDeletingPost(id, loadingDel) {
    const newPosts = posts.map((p) => ({ ...p }));
    const index = posts.findIndex((post) => post.id === id);
    newPosts[index].isLoadingDelete = loadingDel;
    return newPosts;
  }

  function handleDelete(id) {
    setPosts(isDeletingPost(id, true));
    fetch(`https://jsonplaceholder.typicode.com/posts/${id}`, {
      method: "DELETE",
    })
      .then(() => {
        getPosts();
      })
      .catch((err) => {
        setPosts(isDeletingPost(id, false));
      });
  }

  function goToPreviousPage() {
    if (currentPage === 1) {
      return;
    }
    setCurrentPage(currentPage - 1);
  }

  function goToNextPage() {
    if (currentPage === Math.ceil(100 / postsPerPage)) {
      return;
    }
    setCurrentPage(currentPage + 1);
  }

  function handlePostsPerPageChange(e) {
    setPostsPerPage(e.target.value);
    if (currentPage === 1) {
      return getPosts(e.target.value);
    }
    setCurrentPage(1);
  }

  function searchPosts() {
    // vleren e inputit te search mbaje ne state
    // page number 1, items per page si eshte,
    // query posts
  }

  return (
    <>
      <div className="text-center mt-4">
        <h3>My POSTS API</h3>
      </div>
      <Posts
        error={error}
        setError={setError}
        loadingPosts={loadingPosts}
        setLoadingPosts={setLoadingPosts}
        posts={posts}
        setPosts={setPosts}
        getPosts={getPosts}
        isDeletingPost={isDeletingPost}
        handleDelete={handleDelete}
      />
      <Button
        disabled={currentPage === 1}
        className="ml-4 mr-4"
        onClick={goToPreviousPage}
      >
        Previous
      </Button>
      <Button
        disabled={currentPage === Math.ceil(100 / postsPerPage)}
        onClick={goToNextPage}
        className="mr-4"
      >
        Next
      </Button>

      <select onChange={handlePostsPerPageChange}>
        <option value={5}>5</option>
        <option value={10}>10</option>
        <option value={25}>25</option>
        <option value={50}>50</option>
      </select>
    </>
  );
}
