import React, { useState, useEffect } from "react";
import "./App.css";

import { Container, Row, Table, Button, Col } from "react-bootstrap";
import { Link } from "react-router-dom";

function Posts() {
  const [error, setError] = useState(null);
  const [loadingPosts, setLoadingPosts] = useState(false);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    getPosts();
  }, []);

  function getPosts() {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((res) => res.json())
      .then((results) => {
        setPosts(results);
      })
      .catch((error) => {
        setError(error);
      })
      .finally(() => setLoadingPosts(false));
  }

  function isDeletingPost(id, loadingDel) {
    const newPosts = posts.map((p) => ({ ...p }));
    const index = posts.findIndex((post) => post.id === id);
    newPosts[index].isLoadingDelete = loadingDel;
    return newPosts;
  }

  function handleDelete(id) {
    setPosts(isDeletingPost(id, true));
    fetch(`https://jsonplaceholder.typicode.com/posts/${id}`, {
      method: "DELETE",
    })
      .then(() => {
        getPosts();
      })
      .catch((err) => {
        setPosts(isDeletingPost(id, false));
      });
  }

  function handleDetails(id) {}

  // ndryshosh current page
  // get posts me page numberine ri
  function handlePageChange(pageNumber) {}

  if (error) {
    return <div>Error: {error.message}</div>;
  }
  if (loadingPosts) {
    return <div>LOADING</div>;
  }
  return (
    <Container>
      <Row className="mt-4 mb-4">
        <Col className="text-center">
          <div>
            <h2>POSTS API</h2>
          </div>
        </Col>
        <Col className="text-right">
          <Link to="/create">
            <Button>Create new Post</Button>
          </Link>
        </Col>
      </Row>
      <Row>
        <Table>
          <thead>
            <tr>
              <th>User Id</th>
              <th>Post Id</th>
              <th>Title</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {posts.map((post) => (
              <tr>
                <td>{post.userId}</td>
                <td>{post.id}</td>
                <td>{post.title}</td>
                <td>
                  <div>
                    <Button variant="info" className="mr-3">
                      Details
                    </Button>
                    <Button
                      variant="danger"
                      onClick={() => handleDelete(post.id)}
                    >
                      {post.isLoadingDelete ? "Loading" : "Delete"}
                    </Button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Row>
    </Container>
  );
}

export default Posts;
