import React, { useState } from "react";
import "./App.css";

import { Container, Row, Table, Button } from "react-bootstrap";
import { Link } from "react-router-dom";

function Posts(props) {
  const orderArray = [null, "asc", "desc"];
  const [currentSortOrderIndex, setCurrentSortOrderIndex] = useState(0);
  const [currentSortColumn, setCurrentSortColumn] = useState(null);

  if (props.error) {
    return <div>Error: {props.error.message}</div>;
  }
  if (props.loadingPosts) {
    return <div>LOADING</div>;
  }

  function sortPostsBy(sortByColumn) {
    if (currentSortColumn === sortByColumn) {
      if (currentSortOrderIndex === orderArray.length) {
        setCurrentSortOrderIndex(0);
      }
      setCurrentSortOrderIndex(currentSortOrderIndex + 1);
    }
  }

  return (
    <Container>
      <Row className="mt-4 mb-4 text-right">
        <Link to="/create">
          <Button variant="warning">Create new Post</Button>
        </Link>
      </Row>
      <Row>
        <Table className="mt-5">
          <thead>
            <tr>
              <th>
                <button onClick={() => sortPostsBy("userId")}>User Id</button>
              </th>
              <th>
                <button>Post Id</button>
              </th>
              <th>
                <button>Title</button>
              </th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {props.posts.map((post) => (
              <tr>
                <td>{post.userId}</td>
                <td>{post.id}</td>
                <td>{post.title}</td>
                <td>
                  <div>
                    <Link to={`/details/${post.id}`}>
                      <Button variant="info" className="mr-3">
                        Details
                      </Button>
                    </Link>
                    <Button
                      variant="danger"
                      onClick={() => props.handleDelete(post.id)}
                    >
                      {post.isLoadingDelete ? "Loading" : "Delete"}
                    </Button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Row>
    </Container>
  );
}

export default Posts;
