import React, { useState, useEffect } from "react";
import "./App.css";
import Form from "./components/Form";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  useHistory,
} from "react-router-dom";
import Posts from "./Posts";
import SinglePost from "./components/SinglePost";

function App() {
  const [error, setError] = useState(null);
  const [loadingPosts, setLoadingPosts] = useState(false);
  const [posts, setPosts] = useState([]);
  const [currentPost, setCurrentPost] = useState([]);

  const history = useHistory();

  useEffect(() => {
    getPosts();
  }, []);

  function handleCreation(data) {
    fetch("https://jsonplaceholder.typicode.com/posts", {
      method: "POST",
      body: JSON.stringify({
        title: data.title,
        body: data.body,
        userId: data.userId,
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8",
      },
    });
    history.push("/");
  }

  function getPosts() {
    fetch("https://jsonplaceholder.typicode.com/posts")
      .then((res) => res.json())
      .then((results) => {
        setPosts(results);
      })
      .catch((error) => {
        setError(error);
      })
      .finally(() => setLoadingPosts(false));
  }

  function isDeletingPost(id, loadingDel) {
    const newPosts = posts.map((p) => ({ ...p }));
    const index = posts.findIndex((post) => post.id === id);
    newPosts[index].isLoadingDelete = loadingDel;
    return newPosts;
  }

  function handleDelete(id) {
    setPosts(isDeletingPost(id, true));
    fetch(`https://jsonplaceholder.typicode.com/posts/${id}`, {
      method: "DELETE",
    })
      .then(() => {
        getPosts();
      })
      .catch((err) => {
        setPosts(isDeletingPost(id, false));
      });
  }

  function handleDetails(id) {
    const newPosts = posts.map((p) => ({ ...p }));
    const index = posts.findIndex((post) => post.id === id);
    setCurrentPost(newPosts[index]);
  }

  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <Posts
            error={error}
            setError={setError}
            loadingPosts={loadingPosts}
            setLoadingPosts={setLoadingPosts}
            posts={posts}
            setPosts={setPosts}
            getPosts={getPosts}
            isDeletingPost={isDeletingPost}
            handleDelete={handleDelete}
            handleDetails={handleDetails}
          />
        </Route>
        <Route exact path="/create">
          <Form
            initialValues={{
              userId: "",
              title: "",
              body: "",
            }}
            handleSubmit={handleCreation}
            status="create"
          />
        </Route>
        <Route exact path={`/details/${currentPost.id}`}>
          <SinglePost
            key={currentPost.id}
            post={currentPost}
            handleDelete={handleDelete}
            initialValues={{
              userId: currentPost.userId,
              title: currentPost.title,
              body: currentPost.body,
            }}
            status="edit"
            isLoadingDelete={currentPost.isLoadingDelete}
          />
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
